# Dicionário de correções manuais para falhas do lematizador do spacy (pt_core_news_sm)
DICIONARIO_CORRECOES = {
    "tonturo": "tontura",
    "garganto": "garganta",
    "mancho": "mancha",
    "insônio": "insônia",
    "ansiedader": "ansiedade",
    "diarreiar": "diarreia",
    "diarrear": "diarreia",
    "bocar": "boca",
    "seco": "seca",      # boca seco -> boca seca
    "líquido": "líquida", # fezes líquido -> fezes líquida
    "fraquezar": "fraqueza",
    "bundo": "bunda",
    "temperaturo": "temperatura",
    "alto": "alta",      # temperatura alto -> temperatura alta
}

def limpar_sintomas(texto):
    """
    Recebe um texto cru e retorna apenas substantivos e adjetivos lematizados,
    aplicando correções manuais para erros comuns de lematização.
    """
    doc = nlp(texto.lower())
    
    tokens_limpos = []
    
    for token in doc:
        if not token.is_stop and not token.is_punct:
            if token.pos_ in ['NOUN', 'ADJ', 'PROPN']:
                lemma = token.lemma_
                # Se o próprio token tiver um erro conhecido antes da lematização
                lemma = DICIONARIO_CORRECOES.get(token.text, lemma)
                # Ou se a lematização falhou
                lemma = DICIONARIO_CORRECOES.get(lemma, lemma)
                tokens_limpos.append(lemma)
                
    # Segunda passada para correções de contexto no resultado final
    resultado = " ".join(tokens_limpos)
    
    # Caso especial para "manc ha" que o spacy as vezes separa
    resultado = resultado.replace("manc ha", "mancha")
    
    # Aplica as correções em toda a string para garantir
    for erro, correcao in DICIONARIO_CORRECOES.items():
        # Usamos regex para substituir apenas palavras inteiras
        import re
        resultado = re.sub(rf'\b{erro}\b', correcao, resultado)
            
    return resultado.strip()

